
public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Variable booleana
		boolean booleano;
		booleano = true;
		System.out.println(booleano);
		//Variable entera de tipo byte
		byte bait;
		bait = 64;
		System.out.println(bait);
		//Variable entera de tipo short
		short corta;
		corta = 32000;
		System.out.println(corta);
		//Variable entera de tipo int
		int entera;
		entera = 3294454;
		System.out.println(entera);
		//Variable entera de tipo long
		long larga;
		larga = 1201555656;
		System.out.println(larga);
		//Variable real flotante de tipo float
		float flotante;
		flotante = 2346511.2f;
		System.out.println(flotante);
		//Variable real flotante de tipo double
		double doble;
		doble = 32.21354531321;
		System.out.println(doble);
		//Variable de tipo char
		char caracter;
		caracter = 'b';
		System.out.println(caracter);
		//Cadena de caracteres string
		String cuerda;
		cuerda = "Hola";
		System.out.println(cuerda);
	}

}
