
public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a,b;
		float c=3;
		boolean r,s,t,u,v,w,x;
		a = 3;
		b = 8;
		r = a == 0;
		s = a != 0;
		t = a <= b;
		u = b >= a;
		v = b > a;
		w = b < a;
		x = c == 3.0;
		System.out.println("r:" + r); //false
		System.out.println("s:" + s); //true
		System.out.println("t:" + t); //true
		System.out.println("u:" + u); //true
		System.out.println("v:" + v); //true
		System.out.println("w:" + w); //false
		System.out.println("x:" + x); //true
		
		/*El resultado de lo anterior son operaciones logicas porque se utilizan operadores logicos
		 * y dan resultados booleanos*/
	}

}
